#
# Структура таблицы `lessons`
#

CREATE TABLE `lessons` (
  `id` int(4) NOT NULL auto_increment,
  `title` varchar(255) NOT NULL default '',
  `meta_d` varchar(255) NOT NULL default '',
  `meta_k` varchar(255) NOT NULL default '',
  `date` date NOT NULL default '0000-00-00',
  `description` text NOT NULL,
  `text` text NOT NULL,
  `author` varchar(255) NOT NULL default '',
  PRIMARY KEY  (`id`)
) TYPE=MyISAM AUTO_INCREMENT=5 ;

#
# Дамп данных таблицы `lessons`
#

INSERT INTO `lessons` VALUES (1, 'Как заработать с Google Adsense', 'Пошаговая инструкция регистрации', 'Google,adsense', '2007-08-28', '<p>В этом уроке мы научимся регистрироваться в системе \r\n          контекстной рекламы, которая поможет нам заработать деньги на своём \r\n          сайте.</p>', '<p>В этом уроке мы научимся регистрироваться в системе \r\n          контекстной рекламы, которая поможет нам заработать деньги на своём \r\n          сайте.</p>\r\n                    \r\n        <p align="center"><font size="4" face="Verdana, Arial, Helvetica, sans-serif"><strong>Пример \r\n          заработка с двух обыкновенных сайтов за 3-4 месяца, не прилагая особых \r\n          усилий:</strong></font></p>\r\n    \r\n                  <p align="center"><img src="lessons/280807/google/1.gif" width="452" height="148"></p>\r\n      <p>Что же это за система такая, которая позволяет заработать \r\n        на сайте, даже если на нём ничего не продается?</p>\r\n      <p>Система называется <strong>Google Adsense</strong>. </p>\r\n        <p align="center"><font size="4" face="Verdana, Arial, Helvetica, sans-serif"><strong>Как \r\n          всё это работает?</strong></font></p>\r\n      <p>Смысл в том, что Вы регистрируете один свой сайт в системе \r\n        контекстной рекламы <strong>Google Adsense</strong>. После регистрации, \r\n        Вам выдают специальный Javascript код, который Вы можете размещать в любом \r\n        месте любого своего сайта (который соответствует требованиям системы).</p>\r\n      <p>Теперь, при загрузке Вашего сайта, вместо этого Javascript кода будет \r\n        подгружаться реклама, которая подходит по смыслу к тематике Вашего ресурса.</p>\r\n      <p>Примеры с моих сайтов:</p>\r\n<p align="center"><img src="lessons/280807/google/2.gif" width="313" height="154"></p>\r\n<p align="left">Рекламный блок расположен в правой колонке сайта.</p>\r\n<p align="center"><img src="lessons/280807/google/3.gif" width="275" height="182"></p>\r\n      <p align="left">Рекламный блок расположен в левой колонке сайта.</p>\r\n<p>Если посетители Вашего сайта будут кликать по этой рекламе, то Вам будут \r\nкапать деньги. </p>\r\n<p align="center"><img src="lessons/280807/google/4.gif" width="400" height="119"></p>\r\n<p>Сумма за каждый клик зависит от того, сколько за него заплатил рекламодатель \r\n        (обычно это 0,5-2$ за клик). Эта сумма делится между Вами и владельцем \r\n        системы в лучшем случае как 50/50 (если система посчитает Ваш сайт качественным \r\n        ресурсом) в худшем случае как 10/90 (если система посчитает Ваш сайт кучей \r\nмусора).</p>\r\n<p align="center"><img src="lessons/280807/google/5.gif" width="312" height="98"></p>', 'Евгений Попов');
INSERT INTO `lessons` VALUES (2, 'Как открыть счет в Rupay', 'Инструкция по открытию счета в Рупей', 'Rupay, счет, открыть', '2007-08-28', '<p>В этом уроке пойдет речь об открытие счета в системе \r\n          Rupay.</p>\r\n        <p>Вообще, счёт в этой системе понадобится Вам для многих \r\n          целей. Например, Вы сможете без проблем покупать в Интернете цифровые \r\n          товары, оплачивать хостинг, принимать деньги от своих клиентов и пр.</p>\r\n      ', '<p>!!!В этом уроке пойдет речь об открытие счета в системе \r\n          Rupay.</p>\r\n        <p>Вообще, счёт в этой системе понадобится Вам для многих \r\n          целей. Например, Вы сможете без проблем покупать в Интернете цифровые \r\n          товары, оплачивать хостинг, принимать деньги от своих клиентов и пр.</p>\r\n        <p>Распишем процесс открытия счета по шагам:</p>\r\n        <p><strong>1.</strong> Пройдите по ссылке - <a href="http://ruseller.com/p/rupay.php" target="_blank">открыть \r\n          счёт в Rupay</a></p>\r\n        <p align="center"><img src="lessons/280807/rupay/1.gif" width="212" height="108"></p>\r\n        <p><strong>2.</strong> На открывшейся странице жмите по \r\n          ссылке в правом меню, указанной на рисунке:</p>\r\n        <p align="center"><img src="lessons/280807/rupay/2.gif" width="219" height="98"></p>\r\n        <p><strong>3.</strong> Затем, заполняйте регистрационную \r\n          форму и жмите кнопку: "Регистрация".</p>\r\n        <p align="center"><img src="lessons/280807/rupay/3.gif" width="214" height="91"></p>\r\n        <p><strong>4.</strong> Итак, если всё прошло хорошо то \r\n          Вы стали владельцем счета, который не мешало бы пополнить. Пополнить \r\n          счёт можно множеством различных способов. Подробнее об этом Вы узнаете \r\n          если нажмете по ссылке "пополнить счет".</p>\r\n        <p align="center"><img src="lessons/280807/rupay/4.gif" width="236" height="86"></p', 'Евгений Попов');

